import React, { useState } from "react";
import axios from "axios";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import VisitorLog from "./VisitorLog";
import { motion } from "framer-motion";

function Home() {
    const [result, setResult] = useState("");
    const [showAddPrompt, setShowAddPrompt] = useState(false);
    const [nameInput, setNameInput] = useState("");

    const handleClick = async () => {
        const audio = new Audio("/bell.mp3");
        audio.play();
        setResult("Capturing...");
        try {
            const res = await axios.get("http://localhost:5000/capture");
            if (res.data.match) {
                setResult(`‚úÖ Hello ${res.data.name}!`);
            } else {
                setResult("‚ùå Face not recognized.");
            }
        } catch (error) {
            setResult("‚ö†Ô∏è Error: " + error.message);
        }
    };

    return (
        <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.6 }}
            style={{
                textAlign: "center",
                backgroundColor: "#e6ffe6", // light green background
                padding: "20px",
                border: "8px solid brown",
                height: "100vh",
            }}
        >
            {/* Logo Image */}
            <img src="/logo.jpg" alt="Logo" width="150" />

            <h1>üé• Doorbell Face Recognition</h1>

            <motion.button
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.95 }}
                onClick={handleClick}
                style={{
                    width: "200px",
                    height: "60px",
                    fontSize: "18px",
                    padding: "10px",
                    borderRadius: "8px",
                    backgroundColor: "#4CAF50",
                    color: "white",
                    border: "none",
                    cursor: "pointer",
                }}
            >
                üì∏ Capture Face
            </motion.button>

            {result && (
                <p
                    style={{
                        fontSize: "36px",
                        backgroundColor: "lightblue",
                        padding: "10px",
                        borderRadius: "8px",
                        marginTop: "40px",
                    }}
                >
                    {result}
                </p>
            )}

            <Link to="/log">
                <button
                    style={{
                        marginTop: "30px",
                        padding: "10px 20px",
                        fontSize: "16px",
                        borderRadius: "5px",
                    }}
                >
                    üìÑ View Visitor Log
                </button>
            </Link>
        </motion.div>
    );
}

function App() {
    return (
        <Router>
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/log" element={<VisitorLog />} />
            </Routes>
        </Router>
    );
}

export default App;
