import React, { useState } from "react";
import axios from "axios";

function App() {
  const [result, setResult] = useState("Click to capture face");

  const handleClick = async () => {
    const audio = new Audio("/doorbell.mp3");
    audio.play();
    setResult("Capturing...");
    try {
      const res = await axios.get("http://localhost:5000/capture");
      if (res.data.match) {
        setResult(`‚úÖ Hello ${res.data.name}!`);
      } else {
        setResult("‚ùå Face not recognized.");
      }
    } catch (error) {
      setResult("‚ö†Ô∏è Error: " + error.message);
    }
  };

  return (
  <div
    style={{
      textAlign: "center",
      marginTop: "150px",
      backgroundColor: "#e6ffe6", // light blue background
      minHeight: "100vh", // full height
      paddingTop: "50px",
    }}
  >
    <h1>üé• Doorbell Face Recognition</h1>
    <button onClick={handleClick}>üì∏ Capture Face</button>
    <p style={{ marginTop: "20px", fontSize: "18px" }}>{result}</p>
  </div>
);

}

export default App;
