import React, { useState } from "react";
import axios from "axios";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import VisitorLog from "./VisitorLog";

function Home() {
    const [result, setResult] = useState("");

    const handleClick = async () => {
        const audio = new Audio("/bell.mp3");
        audio.play();
        setResult("Capturing...");
        try {
            const res = await axios.get("http://localhost:5000/capture");
            if (res.data.match) {
                setResult(`‚úÖ Hello ${res.data.name}!`);
            } else {
                setResult("‚ùå Face not recognized.");
            }
        } catch (error) {
            setResult("‚ö†Ô∏è Error: " + error.message);
        }
    };

    return (
        <div
            style={{
                textAlign: "center",
                backgroundColor: "#e6ffe6", // light blue background
                padding: "20px",
                border: "8px solid brown",
                padding: "20px",
                height: "100vh",
            }}
        >
            <div
                style={{
                    textAlign: "center",
                    paddingLeft: "20px",
                    paddingRight: "20px",
                    paddintBottom: "20px",
                    paddingTop: "0.001px",
                    border: "8px solid brown",
                    height: "100%",
                }}
            >
                <h1>üé• Doorbell Face Recognition</h1>
                <button onClick={handleClick}>üì∏ Capture Face</button>
                {result !== "" ? (
                    <p style={{ marginTop: "20px", fontSize: "18px" }}>
                        {result}
                    </p>
                ) : (
                    <br />
                )}
                <Link to="/log">
                    <button style={{ marginTop: "30px" }}>
                        üìÑ View Visitor Log
                    </button>
                </Link>
            </div>
        </div>
    );
}

function App() {
    return (
        <Router>
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/log" element={<VisitorLog />} />
            </Routes>
        </Router>
    );
}

export default App;
